@section('scripts')
document.addEventListener('DOMContentLoaded', function () {
    function togglePasswordVisibility(event) {
        const userId = event.target.closest('button').id.split('-')[1];
        const isConfirmPassword = event.target.closest('button').id.includes('Confirm');
        const input = document.getElementById(isConfirmPassword ? `confirmPassword-${userId}` : `password-${userId}`);
        const eyeIcon = document.getElementById(isConfirmPassword ? `eyeIconConfirm-${userId}` : `eyeIcon-${userId}`);
        const eyeSlashIcon = document.getElementById(isConfirmPassword ? `eyeSlashIconConfirm-${userId}` : `eyeSlashIcon-${userId}`);

        if (input.type === 'password') {
            input.type = 'text';
            eyeIcon.classList.add('hidden');
            eyeSlashIcon.classList.remove('hidden');
        } else {
            input.type = 'password';
            eyeIcon.classList.remove('hidden');
            eyeSlashIcon.classList.add('hidden');
        }
    }

    document.querySelectorAll('[id^="togglePassword-"], [id^="toggleConfirmPassword-"]').forEach(toggleButton => {
        toggleButton.addEventListener('click', togglePasswordVisibility);
    });
});
document.addEventListener('DOMContentLoaded', function () {
    const togglePassword = document.getElementById('togglePassword');
    const password = document.getElementById('password');
    const eyeIcon = document.getElementById('eyeIcon');
    const eyeSlashIcon = document.getElementById('eyeSlashIcon');

    const toggleRepeatPassword = document.getElementById('toggleRepeatPassword');
    const repeatPassword = document.getElementById('repeatPassword');
    const eyeIconRepeat = document.getElementById('eyeIconRepeat');
    const eyeSlashIconRepeat = document.getElementById('eyeSlashIconRepeat');

    togglePassword.addEventListener('click', function () {
        const type = password.getAttribute('type') === 'password' ? 'text' : 'password';
        password.setAttribute('type', type);

        eyeIcon.classList.toggle('hidden');
        eyeSlashIcon.classList.toggle('hidden');
    });

    toggleRepeatPassword.addEventListener('click', function () {
        const type = repeatPassword.getAttribute('type') === 'password' ? 'text' : 'password';
        repeatPassword.setAttribute('type', type);

        eyeIconRepeat.classList.toggle('hidden');
        eyeSlashIconRepeat.classList.toggle('hidden');
    });
});
@endsection